func DashboardAlertsNameGet(w http.ResponseWriter, r *http.Request) {
	vars := mux.Vars(r)
	name := vars["name"]

	_, err := base64.StdEncoding.DecodeString(r.Header.Get("X-Grafana-CA"))
	if err != nil {
		logrus.Infoln("decode error:", err)
	}
	grafanaURL := strings.TrimRight(r.Header.Get("X-Grafana-Url"), "/")
	grafanaApiKey := r.Header.Get("X-Grafana-API-Key")

	_, id, _, _, found, err := getDashboardByName(name, grafanaURL, grafanaApiKey)

	if err != nil {
		logrus.Errorln("Error retrieving dashboard:", name)
		handleInternalServerError(w, "Error retrieving dashboard: "+name, err.Error())
		return
	}

	if !found {
		handleNotFoundError(w, "Dashboard not found")
		return
	}

	alerts, err := getDashboardAlerts(id, grafanaURL, grafanaApiKey)

	if err != nil {
		logrus.Errorln("Error retrieving alerts for dashboard")
		handleInternalServerError(w, "Error retrieving alerts for dashboard", err.Error())
		return
	}

	var updatedAlerts []GrafanaDashboardAlert

	for _, alert := range alerts {
		var updatedAlert GrafanaDashboardAlert
		updatedAlert = alert
		updatedAlert.Url = grafanaURL + alert.Url
		updatedAlerts = append(updatedAlerts, updatedAlert)
	}

	responseBody, err := json.Marshal(updatedAlerts)

	if err != nil {
		panic(err)
	}

	handleSuccess(w, responseBody)
	return
}

func renderTemplate(name, id, uid, version, templateAsString string) (renderedTemplate io.Reader, err error) {
	templateVars := Variables{name, id, uid, version}
	var payload bytes.Buffer
	t := template.New("dashboard")
	t.Parse(templateAsString)
	err = t.Execute(&payload, templateVars)

	logrus.Debugln("Rendered dashboard template:")
	logrus.Debugln(t.Execute(os.Stdout, templateVars))

	if err != nil {
		return
	}
	renderedTemplate = &payload
	return renderedTemplate, err
}

func DashboardNamePut(w http.ResponseWriter, r *http.Request) {
	vars := mux.Vars(r)
	name := vars["name"]

	_, err := base64.StdEncoding.DecodeString(r.Header.Get("X-Grafana-CA"))
	if err != nil {
		logrus.Errorln("decode error:", err)
	}
	grafanaURL := strings.TrimRight(r.Header.Get("X-Grafana-Url"), "/")
	grafanaApiKey := r.Header.Get("X-Grafana-API-Key")
	reqBody, err := ioutil.ReadAll(r.Body)

	if err != nil {
		panic(err)
	}

	if len(reqBody) == 0 {
		logrus.Infoln("Malformed request body:", string(reqBody))
		handleBadRequest(w, "request body malformed")
		return
	}

	var t TemplateUrl
	err = json.Unmarshal(reqBody, &t)
	if err != nil || t.Url == "" {
		logrus.Infoln("Malformed request body:", string(reqBody))
		handleBadRequest(w, "request body malformed")
		return
	}

	templateUrl := t.Url
	logrus.Infoln("PUT request for name:", name, "templateUrl:", templateUrl)
	templateFromUrl, err := getTemplateFromUrl(templateUrl)

	if err != nil {
		handleInternalServerError(w, "internal server error", "error fetching template from templateUrl")
		return
	}

	url, id, uid, version, found, err := getDashboardByName(name, grafanaURL, grafanaApiKey)

	if err != nil {
		handleInternalServerError(w, "internal server error", "error searching for dashboard in Grafana")
		return
	}

	var idVar, uidVar, versionVar string
	if found == false {
		idVar, uidVar, versionVar = "null", "null", "1"
		logrus.Infoln("Dashboard not found, creating new")
	} else {
		idVar, uidVar = strconv.FormatInt(id, 10), "\""+uid+"\""
		versionVar = strconv.FormatInt(version+1, 10)
		logrus.Infof("Dashboard found id: %s uid: %s version: %s", idVar, uidVar, strconv.FormatInt(version, 10))
		logrus.Infof("Updating existing dashboard id: %s uid: %s version: %s", idVar, uidVar, versionVar)
	}

	logrus.Infof("Attempting to create dashboard for name %s in grafana", name)

	renderedTemplateFromUrl, err := renderTemplate(name, idVar, uidVar, versionVar, string(templateFromUrl))

	if err != nil {
		logrus.Errorln(err)
		return
	}

	status, body, err := callGrafana(false, grafanaURL+"/api/dashboards/db", grafanaApiKey, "POST", renderedTemplateFromUrl)

	if status != 200 || err != nil {
		logrus.Errorln(err)
		handleInternalServerError(w, "internal server error", "error creating dashboard from template: "+templateUrl)
		return
	}

	var g GrafanaDashboard
	if err := json.Unmarshal(body, &g); err != nil {
		handleInternalServerError(w, "internal server error", "Unexpected response from Grafana")
		return
	}

	url = grafanaURL + g.Url
	id = g.Id
	uid = g.Uid

	var dashboard GrafanaDashboard
	dashboard = GrafanaDashboard{Name: name, Url: url, Id: id, Uid: uid}
	responseBody, err := json.Marshal(dashboard)

	handleSuccess(w, responseBody)
	return
}

